package com.mycompany.services.utils;import java.io.Serializable;public class Category implements Serializable{	/**	 * 	 */	private static final long serialVersionUID = 1L;		private String nom;	private String valeur;		public String getValeur() {		return valeur;	}	public void setValeur(String valeur) {		this.valeur = valeur;	}	public String getNom() {		return nom;	}	public void setNom(String nom) {		this.nom = nom;	}	}